mapper.insert:
INSERT INTO items (
	item_category_name
  , item_name
  , item_price
  , item_sale_price
  , item_stock
  , item_refund_yn
  , item_content
  ) 
  VALUES (
  	#{itemCategoryName}
  , #{itemName}
  , #{itemPrice}
  , #{itemSalePrice}
  , #{itemStock}
  , #{itemRefundYn}
  , #{itemContent} 
)

mapper.update:
UPDATE 
	items i, tb_attach t
SET 
	i.item_category_name = #{itemCategoryName}
  , i.item_name = #{itemName}
  , i.item_content = #{itemContent}
  , i.item_price = #{itemPrice}
  , i.item_sale_price = #{itemSalePrice}
  , i.item_stock = #{itemStock}
  , i.item_refund_yn = #{itemRefundYn}
  , i.item_no = #{itemNo}
  , t.original_name = #{originalName}
  , t.item_idx = #{itemIdx}
WHERE
	i.item_no = t.item_idx
AND 
	i.item_no = #{itemNo}
